  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'We-Make-Website';

  @ViewChild('sidenav') sidenav!: MatSidenav ;

  opened: boolean | undefined;

  constructor() { }

  ngOnInit() {
  }

  clickHandler() {
    this.sidenav.close();
  }
}
